mapper.select:
SELECT a.deviceTotal, b.rackTotal, c.orgTotal, d.roomTotal, 1 level, e.deviceTypeTotal FROM ( ( SELECT COUNT(rs.id) 'deviceTotal' FROM res rs LEFT JOIN sys_org so ON so.id = rs.org_id WHERE
so.full_code LIKE CONCAT('',#{fullCode},'%')
AND rs.is_del = 0 AND `status` = '1'
and rs.region_id = #{regionId}
) a, ( SELECT COUNT(rk.id) 'rackTotal' FROM rack rk LEFT JOIN sys_org so ON so.id = rk.org_id WHERE
so.full_code LIKE CONCAT('',#{fullCode},'%')
AND rk.is_del = 0
and rk.region_id = #{regionId}
) b, ( SELECT COUNT(so.id) 'orgTotal' FROM sys_org so WHERE so.is_del = 0
AND so.full_code LIKE CONCAT('',#{fullCode},'%')
and so.region_id = #{regionId}
) c, ( SELECT COUNT(rm.id) 'roomTotal' FROM room rm LEFT JOIN sys_org so ON rm.org_id = so.id WHERE
so.full_code LIKE CONCAT('',#{fullCode},'%')
AND rm.is_del = 0
and rm.region_id = #{regionId}
) d, ( SELECT COUNT(rt.id) 'deviceTypeTotal' FROM res_type rt where rt.is_del=0 ) e )

SELECT DATE_FORMAT(a.created_time, '%Y') year, COUNT(DISTINCT(altd.res_id))'allocationTotal', COUNT(DISTINCT(rtd.res_id))'repairTotal', COUNT(DISTINCT(btd.res_id)) 'borrowTotal', sum( CASE WHEN a.`status` = 6 THEN 1 ELSE 0 END ) 'scrapTotal' FROM ( SELECT rs.* FROM res rs LEFT JOIN sys_org so ON rs.org_id = so.id
WHERE so.full_code LIKE CONCAT('',#{fullCode},'%')
AND rs.region_id=#{regionId}
) a LEFT JOIN allocation_task_desc altd on a.id=altd.res_id and altd.is_del=0 LEFT JOIN repair_task_desc rtd on a.id=rtd.res_id and rtd.is_del=0 LEFT JOIN borrow_task_desc btd on a.id=btd.res_id and btd.is_del=0 GROUP BY DATE_FORMAT(a.created_time, '%Y')

SELECT IFNULL( sum( CASE WHEN rs.`status` = '1' THEN 1 ELSE 0 END ), 0 ) 'onlineTotal', IFNULL( sum( CASE WHEN rs.`status` = '0' THEN 1 ELSE 0 END ), 0 ) 'unknownTotal', IFNULL( sum( CASE WHEN rs.`status` = '6' THEN 1 ELSE 0 END ), 0 ) 'scrapTotal', IFNULL( sum( CASE WHEN rs.`status` = '4' THEN 1 ELSE 0 END ), 0 ) 'repairTotal', IFNULL( sum( CASE WHEN rs.`status` = '5' THEN 1 ELSE 0 END ), 0 ) 'borrowTotal', IFNULL( sum( CASE WHEN rs.`status` = '3' THEN 1 ELSE 0 END ), 0 ) 'allocationTotal' FROM res rs LEFT JOIN sys_org so ON rs.org_id = so.id WHERE rs.is_del=0
AND so.full_code LIKE CONCAT('',#{fullCode},'%')
AND rs.region_id=#{regionId}
